    <NavItem className=""            
                  href="gallery">
              Galeria
    </NavItem>
    <NavItem className=""
                  
                  href="contact">
              Kontakt
    </NavItem>
    <NavItem>
    <Modal
    actions={[
      <Button flat modal="close" node="button" waves="green">Close</Button>
    ]}
    bottomSheet={false}
    fixedFooter={false}
    header="RODO"
    id="Modal-10"
    open={false}
    options={{
      dismissible: true,
      endingTop: '10%',
      inDuration: 250,
      onCloseEnd: null,
      onCloseStart: null,
      onOpenEnd: null,
      onOpenStart: null,
      opacity: 0.5,
      outDuration: 250,
      preventScrolling: true,
      startingTop: '4%'
    }}
  
    trigger={<Button className="blue" node="button">RODO</Button>}
  >
